/* eslint-disable jsx-a11y/alt-text */
import React from 'react';
import Sidebar from '../components/sidebar';
import Header from '../components/header';

const http = new XMLHttpRequest();
const API_URL = "http://localhost:3000/api/scooter";

class LogAddsScooterin extends Component {
    constructor(props){
      super(props);
      this.state = {
          correo: '',
          clave: ''
      };
    }
    login = () => {
        let tabla = "persona";

        let data = `{
          "tabla": "${tabla}", 
          "datos":
            {
              "persona_email": "${ this.state.correo }",
              "persona_clave": "${this.state.clave}"
            }
         }`;

    handleCorreo = text => { this.setState({ correo: text }) }
    handleClave = text => { this.setState({ clave: text }) }
    http.open("POST", API_URL, true);
    http.setRequestHeader("Content-Type", "application/json");

    if (this.state.correo === "" || this.state.clave === "") {
      alert("Complete todos los datos para continuar...");
    } else {
      http.send(data);
      alert("Se ha registrado correctamente");
      // window.location.assign("./login.html");
    }
  };

  render() {
    return (
        <div>
            <Sidebar />,
            <Header />,
            <div className="component_position">
                <main className="my-8">
                    <p className="text-center">Bienvenido a la sección para agregar un nuevo Scooter.</p>
                </main>
            </div>

            <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 flex flex-col my-2">
                <div className="-xl-3 md:flex sm-6">
                    <div className="md:w-1/2 px-3 mb-6 md:mb-0">
                        <label className="block uppercase tracking-wide text-grey-darker text-xs font-bold mb-2">Descripcion</label>
                        <textarea className="appearance-none block w-full bg-grey-lighter text-grey-darker border border-red rounded py-3 px-4 mb-3" id="grid-first-name" type="text" placeholder="Roger Cedeño"></textarea>
                    </div>
                    <div className="md:w-1/2 px-3 mb-6 md:mb-0">
                        <select name="select">
                            <option value="1">Desocupado</option>
                            <option value="2">Ocupado</option>
                        </select>
                    </div>
                    <div className="md:w-1/2 px-3 mb-6 md:mb-0">
                        <label className="block uppercase tracking-wide text-grey-darker text-xs font-bold mb-2">Codigo</label>
                        <input className="appearance-none block w-full bg-grey-lighter text-grey-darker border border-red rounded py-3 px-4 mb-3" id="grid-first-name" type="text"></input>
                    </div>
                </div>
                <div className="flex items-center justify-between">
                    <button className="bg-blue hover:bg-blue-dark text-black font-bold py-2 px-4 rounded" type="submit">Guadar</button>
                </div>
            </div>
        </div>
    )
  }
}

export default AddsScooter;
